import React from 'react';
import { useSelector } from 'react-redux';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import { Container, Typography, Button, AppBar, Toolbar, IconButton } from '@mui/material';
import { Link } from 'react-router-dom';
import MenuIcon from '@mui/icons-material/Menu';
import '../index.css';

const Home = () => {
  const { currentUser } = useSelector((state) => state.user);
  const theme = createTheme({
    palette: {
      primary: {
        main: '#79A76B', // Sage green
        light: '#79A76B', // Sage garden
        dark: '#4A5D23', // Darker sage green
      },
      secondary: {
        main: '#FF9800', // Bold orange
        light: '#FFB74D', // Lighter orange
        dark: '#F57C00', // Darker orange
      },
      background: {
        default: '#C7D1C5', // Frosted Sage 
        paper: '#F5F5F5', // Light grey surfaces
      },
      text: {
        primary: '#212121', // Dark grey text
        secondary: '#757575', // Medium grey text
      },
    },
    typography: {
      fontFamily: '"Nunito", "Roboto", "Helvetica", "Arial", sans-serif',
      h1: {
        fontWeight: 600,
        fontSize: '2.5rem',
      },
      h2: {
        fontWeight: 600,
        fontSize: '2rem',
      },
      h4: {
        fontWeight: 600, 
        fontSize: '2rem',
      },
      body1: {
        fontWeight: 400, 
        fontSize: '1rem',
      },
      h6: {
        fontWeight: 900,
        fontSize: '2rem',
      }

    },
  });

  return (
    <div>
      <ThemeProvider theme={theme}>
        <AppBar position="static">
          <Toolbar>
            <IconButton edge="start" color="inherit" aria-label="menu" sx={{ mr: 2 }}>
              <MenuIcon />
            </IconButton>
            <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
              SafeBites
            </Typography>
            <Button color="inherit" component={Link} to="/restaurants">
              Restaurants
            </Button>
            <Button color="inherit" component={Link} to="/recipes">
              Recipes
            </Button>
            <Button color="inherit" component={Link} to="/profile">
              Profile
            </Button>
          </Toolbar>
        </AppBar>
        <Container sx={{ bgcolor: theme.palette.background.default, minHeight: '100vh' }}>
          <Typography variant="h4" component="h1" gutterBottom>
            Welcome, {currentUser ? currentUser.username : 'Guest'}
          </Typography>
          <Typography variant="body1" paragraph>
            {/* Add more content here if needed */}
          </Typography>
        </Container>
      </ThemeProvider>

    </div>
  );
};

export default Home;
